import Square from './Square';
import calculateWinner from './calculateWinner';
function Board({xIsNext, squares, onPlay}) {
    const calc = calculateWinner(squares);
    const winner = calc.player;
    const winningLine = calc.line;
    console.log(winningLine);
    let status;
    if (winner) {
      status = "Winner: " + winner;

    } else if(squares.includes(null)) {
      status = "Next player: " + (xIsNext ? "X" : "O");
    } else {
      status = "Game Over, tie";
    }
  
    function handleClick(i) {
      if (squares[i] || calculateWinner(squares).player) {
        return;
      }
      const nextSquares = squares.slice();
      if (xIsNext) {
        nextSquares[i] = "X";
      } else {
        nextSquares[i] = "O";
      }
      onPlay(nextSquares);
    }
    
    return (
      <>
      <div className="status">
        {status}
      </div>
        <div className="board-row">
          <Square value={squares[0]} onSquareClick={() => handleClick(0)} highlight={winningLine && winningLine.includes(0)}/>
          
          <Square value={squares[1]} onSquareClick={() => handleClick(1)} highlight={winningLine && winningLine.includes(1)}/>
          <Square value={squares[2]} onSquareClick={() => handleClick(2)} highlight={winningLine && winningLine.includes(2)}/>
        </div>
        <div className="board-row">
          <Square value={squares[3]} onSquareClick={() => handleClick(3)} highlight={winningLine && winningLine.includes(3)}/>
          <Square value={squares[4]} onSquareClick={() => handleClick(4)} highlight={winningLine && winningLine.includes(4)}/>
          <Square value={squares[5]} onSquareClick={() => handleClick(5)} highlight={winningLine && winningLine.includes(5)}/>
        </div>
        <div className="board-row">
          <Square value={squares[6]} onSquareClick={() => handleClick(6)} highlight={winningLine && winningLine.includes(6)}/>
          <Square value={squares[7]} onSquareClick={() => handleClick(7)} highlight={winningLine && winningLine.includes(7)}/>
          <Square value={squares[8]} onSquareClick={() => handleClick(8)} highlight={winningLine && winningLine.includes(8)}/>
        </div>
      </>
    );
  
  }
export default Board;
